mapper.select:
select permission
from a407.member
where gId = #{gId} and nickname = #{nickname}

select member.mId, member.email, a407.group.gId, a407.group.groupName, member.permission
from member, a407.group
where member.email = #{email} and member.gId = a407.group.gId;

mapper.update:
update member
set permission = #{permission}
where mId = #{mId}
